        uint256 _totalRewardsDistributed,
        uint256 _rewardPerSecond,
        uint256 _contractBalance
    ) {
        return (
            totalStaked,
            totalRewardsDistributed,
            rewardPerSecond,
            fanToken.balanceOf(address(this))
        );
    }
